4b\u0439 \u0440\u0435\u0436\u0438\u0441\u0441\u0435\u0440 \u043f\u043e\u0441\u0432\u044f\u0442\u0438\u043b \u0441\u0432\u043e\u0435\u0439 \u0434\u043e\u0447\u0435\u0440\u0438 \u041d\u0430\u0441\u0442\u0435. \u0412 2011 \u0433\u043e\u0434\u0443 \u043f\u0440\u0438 \u043d\u0435\u0438\u0437\u0432\u0435\u0441\u0442\u043d\u044b\u0445 \u043e\u0431\u0441\u0442\u043e\u044f\u0442\u0435\u043b\u044c\u0441\u0442\u0432\u0430\u0445 \u043f\u043e\u0433\u0438\u0431\u043b\u0430 \u0436\u0435\u043d\u0430 \u041b\u0435\u043e\u0441\u0430 \u041a\u0430\u0440\u0430\u043a\u0441\u0430 \u2013 \u0415\u043a\u0430\u0442\u0435\u0440\u0438\u043d\u0430 \u0413\u043e\u043b\u0443\u0431\u0435\u0432\u0430. \u0418 \u044d\u0442\u043e \u0437\u0430\u0441\u0442\u0430\u0432\u043b\u044f\u0435\u0442 \u0437\u0430\u0434\u0443\u043c\u044b\u0432\u0430\u0442\u044c\u0441\u044f \u043e\u0431 \u0430\u0432\u0442\u043e\u0431\u0438\u043e\u0433\u0440\u0430\u0444\u0438\u0447\u043d\u043e\u0441\u0442\u0438 \u043a\u0430\u0440\u0442\u0438\u043d\u044b, \u0447\u0442\u043e, \u043a\u043e\u043d\u0435\u0447\u043d\u043e \u0436\u0435, \u0434\u0435\u043b\u0430\u0435\u0442 \u0435\u0449\u0435 \u0431\u043e\u043b\u044c\u043d\u0435\u0435. \u0413\u043b\u0430\u0432\u043d\u044b\u0439 \u0433\u0435\u0440\u043e\u0439 \u0413\u0435\u043d\u0440\u0438 \u2013 \u043a\u043e\u043c\u0438\u043a-\u043f\u0440\u043e\u0432\u043e\u043a\u0430\u0442\u043e\u0440, \u0435\u0433\u043e \u0436\u0435\u043d\u0430 \u042d\u043d\u043d \u2013 \u043e\u043f\u0435\u0440\u043d\u0430\u044f \u043f\u0435\u0432\u0438\u0446\u0430. \u041e\u043d\u0438 \u043f\u043e\u043b\u043d\u044b\u0435 \u043f\u0440\u043e\u0442\u0438\u0432\u043e\u043f\u043e\u043b\u043e\u0436\u043d\u043e\u0441\u0442\u0438 \u0434\u0440\u0443\u0433 \u0434\u0440\u0443\u0433\u0430. \u041e\u043d\u0430 \u2013 \u0441\u0432\u0435\u0442\u043b\u0430\u044f \u0438 \u0447\u0438\u0441\u0442\u0430\u044f, \u043e\u043d \u2013 \u0442\u0435\u043c\u043d\u044b\u0439 \u0438 \u043c\u0440\u0430\u0447\u043d\u044b\u0439. \u041f\u0440\u043e\u0442\u0438\u0432\u043e\u0440\u0435\u0447\u0438\u0435 \u0437\u0430\u043c\u0435\u0442\u043d\u043e \u0438 \u0432 \u0438\u0445 \u043a\u043e\u0441\u0442\u044e\u043c\u0430\u0445 \u2013 \u042d\u043d\u043d \u0432 \u0436\u0435\u043b\u0442\u044b\u0445 \u0442\u043e\u043d\u0430\u0445, \u0413\u0435\u043d\u0440\u0438 \u2013 \u0432 \u0442\u0435\u043c\u043d\u043e-\u0437\u0435\u043b\u0435\u043d\u044b\u0445. \u0422\u0430\u043a\u0436\u0435 \u044d\u0442\u043e \u043f\u043e\u0434\u0447\u0435\u0440\u043a\u0438\u0432\u0430\u0435\u0442 \u043e\u0434\u0438\u043d \u0438\u0437 \u0434\u0438\u0430\u043b\u043e\u0433\u043e\u0432 \u2013 \u042f \u0443\u0431\u0438\u043b \u0438\u0445. \u2013 \u042f \u0438\u0445 \u0441\u043f\u0430\u0441\u043b\u0430. \u0420\u0435\u0431\u0435\u043d\u043e\u043a \u044d\u0442\u043e\u0439 \u043f\u0430\u0440\u044b \u2013 \u0410\u043d\u043d\u0435\u0442\u0442, \u043f\u0440\u0435\u0434\u0441\u0442\u0430\u0435\u0442 \u043f\u0435\u0440\u0435\u0434 \u043d\u0430\u043c\u0438 \u0432 \u0432\u0438\u0434\u0435 \u0434\u0435\u0440\u0435\u0432\u044f\u043d\u043d\u043e\u0439 \u043a\u0443\u043a\u043b\u044b. \u042d\u0442\u043e\u0442 \u0445\u043e\u0434 \u043a\u0430\u0436\u0435\u0442\u0441\u044f \u0433\u0435\u043d\u0438\u0430\u043b\u044c\u043d\u044b\u043c. \u042d\u0442\u043e\u0442 \u043e\u0431\u0440\u0430\u0437 \u043f\u043e\u043a\u0430\u0437\u044b\u0432\u0430\u0435\u0442 \u043d\u0430\u043c \u0438\u0441\u0442\u0438\u043d\u043d\u043e\u0435 \u043e\u0442\u043d\u043e\u0448\u0435\u043d\u0438\u0435 \u0440\u043e\u0434\u0438\u0442\u0435\u043b\u0435\u0439 \u043a \u0440\u0435\u0431\u0435\u043d\u043a\u0443. \u0410\u043d\u043d\u0435\u0442\u0442 \u0434\u043b\u044f \u043d\u0438\u0445 \u2013 \u043a\u0443\u043a\u043b\u0430, \u043c\u0430\u0440\u0438\u043e\u043d\u0435\u0442\u043a\u0430, \u043e\u043d\u0438 \u0438\u0441\u043f\u043e\u043b\u044c\u0437\u0443\u044e\u0442 \u0435\u0435 \u0438\u0441\u043a\u043b\u044e\u0447\u0438\u0442\u0435\u043b\u044c\u043d\u043e \u0434\u043b\u044f \u0441\u0432\u043e\u0438\u0445 \u0446\u0435\u043b\u0435\u0439. \u0418 \u0442\u043e\u043b\u044c\u043a\u043e \u0432 \u0444\u0438\u043d\u0430\u043b\u044c\u043d\u043e\u0439 \u0441\u0446\u0435\u043d\u0435 \u043e\u043d\u0430 \u043f\u0440\u0435\u0434\u0441\u0442\u0430\u0435\u0442 \u043d\u0430\u0441\u0442\u043e\u044f\u0449\u0438\u043c \u0440\u0435\u0431\u0435\u043d\u043a\u043e\u043c \u2013 \u0442\u043e\u043b\u044c\u043a\u043e \u0442\u043e\u0433\u0434\u0430 \u0413\u0435\u043d\u0440\u0438 \u043d\u0430\u043a\u043e\u043d\u0435\u0446 \u0432\u0438\u0434\u0438\u0442 \u0432 \u043d\u0435\u0439 \u0436\u0438\u0432\u043e\u0433\u043e \u0447\u0435\u043b\u043e\u0432\u0435\u043a\u0430. \u0418\u0445 \u043f\u0435\u0441\u043d\u044f \u0440\u0430\u0437\u0431\u0438\u043b\u0430 \u043c\u043d\u0435 \u0441\u0435\u0440\u0434\u0446\u0435. (\u041e\u0431\u0440\u0430\u0437 \u0413\u0435\u043d\u0440\u0438 \u0432 \u0444\u0438\u043d\u0430\u043b\u0435 \u0441\u043f\u0438\u0441\u0430\u043d \u0441 \u0441\u0430\u043c\u043e\u0433\u043e \u0440\u0435\u0436\u0438\u0441\u0441\u0435\u0440\u0430, \u0432\u0430\u0443!) \u041f\u0435\u0440\u0441\u043e\u043d\u0430\u0436 \u0414\u0440\u0430\u0439\u0432\u0435\u0440\u0430 \u043d\u0430\u0441\u0442\u043e\u043b\u044c\u043a\u043e \u043f\u0443\u0433\u0430\u044e\u0449\u0438\u0439 \u0438 \u0436\u0443\u0442\u043a\u0438\u0439, \u0447\u0442\u043e \u0432\u0435\u0440\u0438\u0442\u044c \u0432 \u0430\u0432\u0442\u043e\u0431\u0438\u043e\u0433\u0440\u0430\u0444\u0438\u0447\u043d\u043e\u0441\u0442\u044c \u044d\u0442\u043e\u0433\u043e \u043e\u0431\u0440\u0430\u0437\u0430 \u0434\u0430\u0436\u0435 \u0441\u0442\u0440\u0430\u0448\u043d\u043e. \u041e\u043d \u0432\u0438\u043d\u0438\u0442 \u0441\u0435\u0431\u044f \u0432 \u0441\u043c\u0435\u0440\u0442\u0438 \u0436\u0435\u043d\u044b, \u0432\u0438\u0434\u0438\u0442 \u0435\u0435 \u0432 \u0410\u043d\u043d\u0435\u0442\u0442, \u0447\u0442\u043e \u0442\u043e\u043b\u044c\u043a\u043e \u0443\u0441\u0438\u043b\u0438\u0432\u0430\u0435\u0442\u0441\u044f \u0435\u0435 \u043d\u0435\u043e\u0431\u044b\u0447\u043d\u044b\u043c \u0434\u0430\u0440\u043e\u043c. \u0418 \u0432 \u043a\u043e\u043d\u0446\u0435, \u043a\u043e\u0433\u0434\u0430 \u043c\u044b \u0432\u0438\u0434\u0438\u043c \u0410\u043d\u043d\u0435\u0442\u0442 \u0436\u0438\u0432\u044b\u043c \u0440\u0435\u0431\u0435\u043d\u043a\u043e\u043c, \u043e\u0441\u043e\u0437\u043d\u0430\u0435\u043c \u0435\u0435 \u043d\u0435\u043d\u0430\u0432\u0438\u0441\u0442\u044c \u043a \u0441\u0432\u0435\u0442\u0443, \u043a \u043f\u0435\u043d\u0438\u044e, \u043a \u0441\u0432\u043e\u0438\u043c \u0440\u043e\u0434\u0438\u0442\u0435\u043b\u044f\u043c, \u0441\u0442\u0430\u043d\u043e\u0432\u0438\u0442\u0441\u044f \u0431\u043e\u043b\u044c\u043d\u043e \u0438 \u0442\u044f\u0436\u0435\u043b\u043e. \u041a\u0441\u0442\u0430\u0442\u0438, \u0431\u0435\u0437 \u044e\u043c\u043e\u0440\u0430 \u0437\u0434\u0435\u0441\u044c \u043d\u0435 \u043e\u0431\u043e\u0448\u043b\u043e\u0441\u044c! \u0421\u0442\u0435\u043d\u0434\u0430\u043f \u0413\u0435\u043d\u0440\u0438 \u043d\u0430\u0437\u044b\u0432\u0430\u0435\u0442\u0441\u044f \u041e\u0431\u0435\u0437\u044c\u044f\u043d\u0430 \u0411\u043e\u0433\u0430, \u043f\u043e\u044d\u0442\u043e\u043c\u0443 \u043e\u043d \u0442\u0430\u043a \u0447\u0430\u0441\u0442\u043e \u0435\u0441\u0442 \u0431\u0430\u043d\u0430\u043d\u044b \u0432 \u043a\u0430\u0434\u0440\u0435 :) \u041a\u043e\u043b\u0438\u0447\u0435\u0441\u0442\u0432\u043e \u043e\u0431\u0435\u0437\u044c\u044f\u043d\u043e\u043a \u0432 \u043a\u0430\u0434\u0440\u0435 (\u043d\u0435 \u0441\u0447\u0438\u0442\u0430\u044f \u0414\u0440\u0430\u0439\u0432\u0435\u0440\u0430) \u043e\u0431\u0438\u043b\u044c\u043d\u043e\u0435. \u0418 \u0431\u043b\u0430\u0433\u043e\u0434\u0430\u0440\u043d\u043e\u0441\u0442\u044c \u042d\u0434\u0433\u0430\u0440\u0443 \u0410\u043b\u043b\u0430\u043d\u0443 \u041f\u043e, \u0443 \u043a\u043e\u0442\u043e\u0440\u043e\u0433\u043e \u0432 \u0423\u0431\u0438\u0439\u0441\u0442\u0432\u0435 \u043d\u0430 \u0443\u043b\u0438\u0446\u0435 \u041c\u043e\u0440\u0433 \u0443\u0431\u0438\u0432\u0430\u043b\u0430 \u043e\u0431\u0435\u0437\u044c\u044f\u043d\u0430 :) \u041f\u043e\u043f\u043e\u043b\u043d\u0438\u043b\u0430 \u0441\u0432\u043e\u0439 \u043f\u043b\u0435\u0439\u043b\u0438\u0441\u0442 \u043f\u043e\u0442\u0440\u044f\u0441\u0430\u044e\u0449\u0438\u043c\u0438 \u043f\u0435\u0441\u043d\u044f\u043c\u0438 \u0438\u0437 \u0444\u0438\u043b\u044c\u043c\u0430.  \u0412 \u043e\u0431\u0449\u0435\u043c, \u0432\u0435\u043b\u0438\u043a\u043e\u043b\u0435\u043f\u043d\u043e\u0435 \u043a\u0438\u043d\u043e, \u043a\u043e\u0442\u043e\u0440\u043e\u0435 \u043e\u0441\u0442\u0430\u043d\u0435\u0442\u0441\u044f \u0432 \u0432\u0430\u0448\u0435\u0439 \u043f\u0430\u043c\u044f\u0442\u0438 (\u0430 \u043c\u043e\u0436\u0435\u0442 \u0438 \u0432 \u0441\u0435\u0440\u0434\u0446\u0435) \u0438 \u0432 \u043f\u043b\u0435\u0439\u043b\u0438\u0441\u0442\u0435. 10 \u0438\u0437 10.\"}]}";
